import React from "react";
import "./Header.css";

const Header = () => {
  const logout = (e) => {
    const userInfo = {
      _id,
    };
    Axios.post("http://localhost:3000/users/logout", userInfo).then(() => {
      cookie.remove("token");
      history("/", { state: { _id: _id, token: token, userId: userId } });
    });
  };

  const myrecipes = (e) => {
    history("/MyRecipes", {
      state: { _id: _id, token: token, userId: userId },
    });
  };

  const addrecipes = (e) => {
    history("/AddRecipes", {
      state: { _id: _id, token: token, userId: userId },
    });
  };

  return (
    <div>
      <header>
        <nav class="main-nav">
          <input type="checkbox" id="check" />
          <label for="check" class="menu-btn">
            <i class="fas fa-bars"></i>
          </label>
          <a href="index.html" class="logo">
            andraWare
          </a>
          <ul class="navlinks">
            <li>
              <a onClick={addrecipes}>Add a Recipe</a>
            </li>
            <li>
              <a onClick={myrecipes}>My Recipes</a>
            </li>
            <li>
              <a href="#">Logout</a>
            </li>
            <li>
              <a href="#" class="contact">
                Deactivate
              </a>
            </li>
          </ul>
        </nav>
      </header>
    </div>
  );
};

export default Header;
